import java.util.concurrent.atomic.AtomicLong
import java.io.BufferedWriter
import java.io.File
import java.io.FileWriter

class Logger {

	private def queue = new java.util.concurrent.ConcurrentLinkedQueue()

	private def quitLock = new Object()

	private def volatile quit = false

	private def thread = null

	public Logger() {
		this.thread = new Thread(new Runnable() {
		def public void run() {

			def user_home = System.getProperty("user.home");
			def dir_path = user_home + "/projectsoapui/testdata";

			BufferedWriter writer = new BufferedWriter(new FileWriter(new File(
				dir_path + "/" + System.currentTimeMillis() + ".txt"	
			)))			

			while(true) {
				synchronized(Logger.this.quitLock) {
					if(Logger.this.quit == true) {
						break;
					}
				}

				def data = Logger.this.queue.poll()
				if(data != null) {
					writer.write(data + "\n")	
				} else  {
					try {
						sleep(2000)
						writer.flush()
					}
					catch(e) {}
				}
			}

			writer.close()
		}
	})

		this.thread.start();
	}

	def public logRequestTimes(context, jsessionid, request) {
		// println "logging"
		queue.add(jsessionid + ";" + context.expand(request.endpoint) + ";" + request.response.timestamp + ";" + request.response.timeTaken)
	}
}

class Scenario {
	def private static rand = new Random()

	def p = 1.0

	def r = 1

	def next = null

	def name = null

	def Scenario(probability, runs, name, next) {
		this.p = Math.ceil(100 * probability)
		this.r = runs
		this.name = name
		this.next = next
	}

	def run(testRunner, context) {
		for(i in 1..this.r) {
			def p = new Float(rand.nextInt(101))
			def run = p <= this.p

			if(run) {
				def start = System.currentTimeMillis()
				println( Thread.currentThread().getId() + " " + this.name + " starting" )
				testRunner.testCase.testSteps[this.name].run(testRunner, context)
				// testRunner.runTestStepByName(this.name)
				println( Thread.currentThread().getId() + " " + this.name + " took " + (System.currentTimeMillis() - start) )

				if(this.next != null) {
					this.next.run(testRunner, context)
				}
			} else {
				println( Thread.currentThread().getId() + " skipping " + this.name + " (" + p + "<" + this.p + ")" )
			}
		}
	}
}

class Context {
	def public shoppinglists = []

	def public favoritelist = []

	def public compareproductids = []

	public String toString() {
		def str = ""
		str += "shoppinglists: " + this.shoppinglists + " \n"
		str += "compareproductids: " + this.compareproductids + "\n"
		str += "favoritelist: " + this.favoritelist + "\n"
		return str
	}
}

class Helpers {
	def private rand = new Random()

	def private context = new java.util.concurrent.ConcurrentHashMap()

	def private logger = new Logger()

	def public getContext(id) {
		assert(id)

		def c = this.context.get(id)
		if(!c) {
			c = new Context() 
			this.context.put(id, c)
		}
		return c
	}

	def public clearContext() {
		this.context = new java.util.concurrent.ConcurrentHashMap()
	}

	def public printContext() {
		println this.context
	}

	def public createScenario(probability, runs, name, next) {
		return new Scenario(probability, runs, name, next)
	}

	def public getInJSessionId(testRunner) {
		def jsessionid = testRunner.testCase.properties["in_jsessionid"].value
		return jsessionid
	}

	def public getQuotedText(text, pattern) {
		def tmp = text.substring(text.indexOf(pattern) + pattern.length())
		def result = tmp.substring(0, tmp.indexOf("\""))
		return result
	}

	def public getRandomB2bUser(testRunner) {

	}

	def public getRandomB2cUser(testRunner) {

	}

	def public getRandomCompareProductId(sessionid) {
		assert(sessionid)

		def c = getContext(sessionid)
		if(c) {
			if(c.compareproductids.size() > 0) {
				return c.compareproductids.get(rand.nextInt(c.compareproductids.size()))
			}
		}
		return null
	}

	def public getRandomProductIdFromFavoriteList(sessionid) {
		assert(sessionid)

		def c = getContext(sessionid)
		if(c) {
			if(c.favoritelist.size() > 0) {
				return c.favoritelist.get(rand.nextInt(c.favoritelist.size()))
			}
		}
		return null
	}

	def public getRandomShoppingList(sessionid) {
		assert(sessionid)

		def c = getContext(sessionid)
		if(c) {
			if(c.shoppinglists.size() > 0) {
				return c.shoppinglists.get(rand.nextInt(c.shoppinglists.size()))
			}
		}
		return null
	}

	def public getRandomProductId(testRunner) {
		def products = testRunner.testCase.testSuite.products
		assert(products)
		assert(products.size() > 0)
		return products.get(rand.nextInt(products.size()))		
	}

	def public getRandomL1CategoryId(testRunner) {
		def categories = testRunner.testCase.testSuite.l1categories
		assert(categories)
		assert(categories.size() > 0)
		return categories.get(rand.nextInt(categories.size()))
	}

	def public getRandomL2CategoryId(testRunner) {
		def categories = testRunner.testCase.testSuite.l2categories
		assert(categories)
		assert(categories.size() > 0)
		return categories.get(rand.nextInt(categories.size()))
	}

	def public getRandomLnCategoryId(testRunner) {
		def categories = testRunner.testCase.testSuite.lncategories
		assert(categories)
		assert(categories.size() > 0)
		return categories.get(rand.nextInt(categories.size()))
	}

	def public getRandomSearchTerm(testRunner) {
		def searchterms = testRunner.testCase.testSuite.searchterms
		assert(searchterms)
		assert(searchterms.size() > 0)
		return searchterms.get(rand.nextInt(searchterms.size()))
	}

	def public getRedirectLocationFromHttpRequest(request) {
		def location = request.response.responseHeaders["Location"]
		assert(location.size() == 1)
		return location[0]
	}

	def public logRequestTimes(context, jsessionid, request) {
		this.logger.logRequestTimes(context, jsessionid, request)
	}

	def public getCookieFromHttpRequest(request, name) {
		assert(request)
		assert(name)

		name = name.toLowerCase()

		def result = ""

		def setCookie = request.response.responseHeaders["Set-Cookie"]
		setCookie.each {
			def tmpSetCookie = it.toLowerCase()
			if(name == "*" || tmpSetCookie.contains(name)) {
				if(result.length() > 0) {
					result += "; "
				}
				result += it.split(";")[0]  
			}
		}		
		return result
	}
}

class FileToListReader {
	private rootDirectory

	public FileToListReader(rootDirectory) {
		assert(rootDirectory)
		if(!rootDirectory.endsWith("/")) {
			rootDirectory += "/"
		}

		this.rootDirectory = rootDirectory
	}

	public read(filename) {
		def results = new ArrayList(10000)


		def file = new File(this.rootDirectory + filename)
		file.eachLine {
			results.add(it)
		}

		return results
	}
}

synchronized(testRunner.testCase.testSuite) {
if(!testRunner.testCase.testSuite.metaClass.hasProperty(testRunner.testCase.testSuite, "helpers")) {
	try {
		log.info("initialization - start")

		def fileToListReader = new FileToListReader(testRunner.testCase.testSuite.project.properties["testdataRoot"].value)

		// reading b2b users
		def b2busers = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("users_b2b"))
		log.info("read " + b2busers.size() + " b2b users")

		// reading b2c users
		def b2cusers = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("users_b2c"))
		log.info("read " + b2cusers.size() + " b2c users")

		// reading l1 categories
		def l1categories = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("categories_l1"))
		log.info("read " + l1categories.size() + " l1 categories")

		// reading l2 categories
		def l2categories = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("categories_l2"))
		log.info("read " + l2categories.size() + " l2 categories")

		// reading ln categories
		def lncategories = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("categories_ln"))
		log.info("read " + lncategories.size() + " ln categories")

		// reading products ids
		def products = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("products"))
		log.info("read " + products.size() + " products")

		// reading search term
		def searchterms = fileToListReader.read(testRunner.testCase.testSuite.getPropertyValue("searchterms"))
		log.info("read " + searchterms.size() + " searchterms")


		// enrich the testSuite object
		testRunner.testCase.testSuite.metaClass.b2busers = b2busers
		testRunner.testCase.testSuite.metaClass.b2cusers = b2cusers
		testRunner.testCase.testSuite.metaClass.l1categories = l1categories
		testRunner.testCase.testSuite.metaClass.l2categories = l2categories
		testRunner.testCase.testSuite.metaClass.lncategories = lncategories
		testRunner.testCase.testSuite.metaClass.products = products
		testRunner.testCase.testSuite.metaClass.searchterms = searchterms

		testRunner.testCase.testSuite.metaClass.runsb2b = new AtomicLong(0)
		testRunner.testCase.testSuite.metaClass.runsb2c = new AtomicLong(0)
		testRunner.testCase.testSuite.metaClass.runsanonymous = new AtomicLong(0)
		testRunner.testCase.testSuite.metaClass.runstotal = new AtomicLong(0)

		def helpers = new Helpers()
		testRunner.testCase.testSuite.metaClass.helpers = helpers

		log.info("initialization - done")
	} catch(e) {
		println "initialization - failed - " + e.message 
		log.error("initialization - failed", e)
	}
	return true
}
